import base64
import json

# Base64编码的Word文档内容
base64_content = ""

# 解码并保存
decoded_content = base64.b64decode(base64_content)
with open("test_network_image_output.docx", "wb") as f:
    f.write(decoded_content)

print("Word文档已保存为: test_network_image_output.docx")
print(f"文件大小: {len(decoded_content)} 字节")



